import { formatMs } from '@material-ui/core';
import React, { Component } from 'react'
import { connect } from 'react-redux';
import  EducationAction  from '../Actions/EducationAction';
class EducationListComponent extends Component {


    componentDidMount(){
        this.props.GetAllJoinUsers(this.props.CourseId);
    }

    render() {
        const {GetAllUsers}=this.props;
        const list_item = items => {

            return (

                items.map(item => {

                    let button;
                    if (item.userId===parseInt(localStorage.getItem('user_'))){
                        button=<Button variant="contained" color="secondary" size="small" onClick={() => this.showEditModal(item.id)}>Düzenle</Button>;
                    }
                    else{
                        button=" ";
                    }
                        return (
                            <TableRow >
                                <TableCell component="th" scope="row">
                                    {item.marketName}
                                </TableCell>
                                <TableCell align="right"> {item.item}</TableCell>
                                <TableCell align="right"> {item.count}</TableCell>
                                <TableCell align="right"> {item.username}</TableCell>
                               

                                <TableCell align="right">{button}</TableCell>

                            </TableRow>

                        )
                })
            );
        }

        return (
           
            <div className="userslist" style={{ padding: 25 }}>
                <TableContainer component={Paper}>
                    
                    <Table size="small" aria-label="a dense table">
                        <TableHead>
                            <p>Kayıt Yaptıranlar Kullanıcılar</p>
                            <TableRow>
                                <TableCell>Kullanıcı </TableCell>
                                <TableCell align="right">Kayıt Yapılan Kurs</TableCell>
                                
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {list_item}
                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
        )
    }
}

const MapStateToProps=_state=>{
    const {GetAllUsers}= _state.EducationReducer;
    return {GetAllUsers};
}

const MapDispatchToProps =(_dispatch, _ownProps) =>{
    return {
        GetAllJoinUsers: (_id) => _dispatch(EducationAction.GetAllJoinUsers(_id)),
    }
}

export const EducationList= connect(MapStateToProps,MapDispatchToProps)(EducationListComponent);
