import React, { Component } from 'react'
import { connect } from 'react-redux';
import MarketAction from '../Actions/MarketAction'
import { TextField, Grid, Button, Modal } from '@material-ui/core';

class AddMarketComponent extends Component {

    state = {
        Subject: '',
        Description: '',
        StartDate: '',
        FinishDate: '',
        UserId:null
    }

    Add = () => {

        const { Subject, Description, StartDate, FinishDate,UserId } = this.state;

        let newMarketList = {
            Subject,
            Description,
            StartDate,
            FinishDate,
            UserId:localStorage.getItem("user_")

        }

        this.props.Add(newMarketList);
    }

    HandleChange =(_e) =>{

        const {name,value} = _e.target;

        this.setState({ [name]:value});
    }


    render() {
        return (
            <div className="add-market" style={{ padding: 50 }}>
                
                    <Grid container spacing={10} alignItems="center">
                        <Grid item>
                            <TextField
                                fullWidth
                                label="Başlangıç Tarihi"
                                type="date"
                                defaultValue="2017-05-24"
                                name="StartDate"
                                onChange={this.HandleChange}
                            />
                        </Grid>
                        <Grid item>
                            <TextField
                                fullWidth
                                label="Bitiş Tarihi"
                                type="date"
                                defaultValue="2017-05-24"
                                name="FinishDate"
                                onChange={this.HandleChange}
                            />
                        </Grid>
                        <Grid item>
                            <TextField

                                onChange={this.HandleChange}
                                name='Subject'
                                autoFocus
                                margin="dense"
                                label="Market Adı"
                                fullWidth
                            />
                        </Grid>
                        <Grid item>
                            <TextField

                                onChange={this.HandleChange}
                                name='Description'
                                autoFocus
                                margin="dense"
                                label="Açıklama"
                                fullWidth
                            />
                        </Grid>
                        <Grid item>
                              <Button style={{ marginTop: 20 }} onClick={this.Add} color="primary" variant="contained"> Ekle</Button>
                        </Grid>

                    </Grid>
                    
                  
                
            </div>
        )
    }
}

const MapStateToProps =_state =>{
    return ;
}

const MapDispatchToProps = (_dispatch, _ownProps) =>{

    return {
        Add: (_newMarketList) => _dispatch(MarketAction.Add(_newMarketList))
    }
}

export const AddMarket= connect(MapStateToProps,MapDispatchToProps)(AddMarketComponent);
