import React, { Component } from 'react'
import { connect } from 'react-redux'
import UserAction from '../Actions/UserAction';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';

class ListMyCoursesComponent extends Component {

    componentDidMount(){
        this.props.GetCourses(this.props.UserId);
    }
    componentDidUpdate(prevProps) {
        if (prevProps.UserCourses) {
            this.render();
        }
    }



    render() {
        const { UserCourses } = this.props;
        if (UserCourses.Data == null) {
            return "";
        }
        const list_item = items => {

            return (

                items.map(item => {

                    let button;
                    if (item.userId === parseInt(localStorage.getItem('user_'))) {
                        button = <Button variant="contained" color="secondary" size="small" onClick={() => this.showEditModal(item.id)}>Düzenle</Button>;
                    }
                    else {
                        button = " ";
                    }
                    return (
                        <TableRow >
                            <TableCell component="th" scope="row">
                                {item.educationSubject}
                            </TableCell>
                            <TableCell align="right"> {item.startDate.toString().slice(0, 10)}</TableCell>
                            <TableCell align="right"> {item.finishDate.toString().slice(0, 10)}</TableCell>
                            <TableCell align="right"> {item.mentorName}</TableCell>


                            <TableCell align="right">{button}</TableCell>

                        </TableRow>

                    )
                })
            );
        }

        const _items = list_item(UserCourses.Data);
        return (
            <div>
                <div className="courselist" style={{ padding: 25 }}>

                    {/* {this.state.ItemId != -1 && (
                        <Modal show={this.state.showEditModal} handleClose={this.hideEditModal}>
                            <Container>
                                <MarketItemUpdate ItemId={this.state.ItemId} />
                            </Container>
                        </Modal>)} */}

                    <TableContainer component={Paper}>

                        <Table size="small" aria-label="a dense table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Kurs</TableCell>
                                    <TableCell align="right">Başlangıç</TableCell>
                                    <TableCell align="right"> &nbsp;&nbsp; Bitiş</TableCell>
                                    <TableCell align="right">Mentor</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {_items}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </div>
            </div>
        )
    }
    
}
const MapStateToProps =_state =>{
    const {UserCourses}=_state.UserReducer;
    return {UserCourses};
}

const MapDispatchToProps=(_dispatch,_ownProps) =>{
    return {
        GetCourses :(_userid) => _dispatch(UserAction.GetCourses(_userid))
    }
}
export const ListMyCourses=connect(MapStateToProps,MapDispatchToProps)(ListMyCoursesComponent);
