import React, { Component } from 'react';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import StoreIcon from '@material-ui/icons/Store';
import AddIcon from '@material-ui/icons/Add';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import { green } from '@material-ui/core/colors';
import { AddMarket } from '../Pages/AddMarket';
import { AddMarketItem } from "../Pages/AddMarketItem";
import Modal from '../Components/Modal';

import { IconButton, TableRow, Container } from '@material-ui/core';
import MarketAction from '../Actions/MarketAction';
import { connect } from 'react-redux';


class MarketComponent extends Component {

    constructor() {
        super();
        this.state = {
            show: false,
            Id: 0,
            StartDate: '',
            FinishDate: '',
            Subject: '',
            Description: '',
            Id: 0,
        };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }

    showModal = () => {
        this.setState({ show: true })
    }

    hideModal = () => {
        this.setState({ show: false })
    }

    AddItem = () => {

    }

    componentDidMount() {
        this.props.GetCurrentMarketList();
    }


    render() {

        const { CurrentMarketList } = this.props;
        if (!CurrentMarketList.Data) {
            console.log("Data yok");
            return " ";

        }

        const item_market = markets => {
            return (
                markets.map(market => {
                    return (
                        <TableRow>
                            <ListItem>
                                <ListItemAvatar>
                                    <Avatar>
                                        <StoreIcon />
                                    </Avatar>
                                </ListItemAvatar>
                                <ListItemText
                                    primary={market.subject}
                                    secondary={market.description}
                                />

                                <Modal show={this.state.show} handleClose={this.hideModal}>
                                    <Container>
                                        <AddMarketItem/>
                                    </Container>
                                </Modal>
                                <IconButton  aria-label="Add" onClick={this.showModal} style={{ display: 'block', margin: 'auto' }}>
                                    <PersonAddIcon />
                                </IconButton>
                            </ListItem>
                        </TableRow>
                    );

                })
            )
        }
        return (
            <div>
                <h3>Market</h3>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <Modal show={this.state.show} handleClose={this.hideModal}>
                                    <Container>
                                        <AddMarket/>
                                    </Container>
                                </Modal>
                                <IconButton className="float-right" edge="end" aria-label="Add" onClick={this.showModal} style={{ display: 'block', margin: 'auto' }}>
                                    <AddIcon style={{ color: green[400] }} />
                                </IconButton>

                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {item_market(CurrentMarketList.Data)}
                        </TableBody>
                    </Table>
                </TableContainer>


            </div>

        )
    }
}
const MapStateToProps = _state => {
    const { CurrentMarketList } = _state.MarketReducer;

    return { CurrentMarketList };
}

const MapDispatchToProps = (_dispatch, _ownProps) => {
    return {
        GetCurrentMarketList: () => _dispatch(MarketAction.GetCurrentMarketList())
    }
}
export const MarketRequest = connect(MapStateToProps, MapDispatchToProps)(MarketComponent);
