import {RequestTool} from '../Tools/RequestTool';

const GetAllItems=(_userId) =>RequestTool.GetRequest("https://localhost:6001/api/User/GetMarkets/"+_userId);

const GetUser=(_id) =>RequestTool.GetRequest("https://localhost:6001/api/User/Get/"+_id);

const GetAllCourses=(_userid) =>RequestTool.GetRequest("https://localhost:6001/api/User/GetCourses/"+_userid);

const RemoveMarketItem=(_item) =>RequestTool.DeleteRequest("https://localhost:6001/api/User/RemoveMarketItem",_item);

const RemoveCourse=(_item) =>RequestTool.DeleteRequest("https://localhost:6001/api/User/RemoveCourse",_item);

export const UserService= {GetAllItems,GetUser,GetAllCourses,RemoveMarketItem,RemoveCourse}
