import {RequestTool} from '../Tools/RequestTool';

const GetAll=() =>RequestTool.GetRequest("https://localhost:6001/api/Market/GetAll");

const Add=(_item) => RequestTool.PostRequest("https://localhost:6001/api/Market/Add",_item);

const GetById=(_item) =>RequestTool.GetRequest("https://localhost:6001/api/Market/Get/"+_item);

const AddMarketItem=(_item) => RequestTool.PostRequest("https://localhost:6001/api/MarketItem/Add",_item);

const GetAllItem=(_id) => RequestTool.GetRequest("https://localhost:6001/api/MarketItem/GetAll/"+_id);

const UpdateItem=(_data) => RequestTool.PutRequest("https://localhost:6001/api/MarketItem/Update",_data);

const GetItemById=(_id) => RequestTool.GetRequest("https://localhost:6001/api/MarketItem/Get/"+_id);

export const MarketService= {GetAll, Add,GetById,AddMarketItem,GetAllItem,UpdateItem,GetItemById}
