import React, { Component } from 'react';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import StoreIcon from '@material-ui/icons/Store';
import AddIcon from '@material-ui/icons/Add';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import { green } from '@material-ui/core/colors';
import {AddMarket} from '../Pages/AddMarket';
import Modal from '../Components/Modal';




import { IconButton, TableRow,Container} from '@material-ui/core';


 class MarketRequest extends Component {

    constructor() {
        super();
        this.state = {
            show: false,
            Id: 0,
            StartDate: '',
            FinishDate: '',
            Subject: '',
            Description:'',
            Id: 0,
        };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }

    showModal = () => {
        this.setState({ show: true })
    }

    hideModal = () => {
        this.setState({ show: false })
    }

    
    render() {
        return (
            <div>
                <h3>Market</h3>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>

                            <Modal show={this.state.show} handleClose={this.hideModal}>
                                    <Container>
                                        <AddMarket />
                                    </Container>
                                </Modal>
                                <IconButton className="float-right" edge="end" aria-label="Add" onClick={this.showModal} style={{ display: 'block', margin: 'auto' }}>
                                    <AddIcon style={{ color: green[400] }} />
                                </IconButton>

                            </TableRow>
                        </TableHead>
                        <TableBody>

                          
                            <TableRow>
                                <ListItem>
                                    <ListItemAvatar>
                                        <Avatar>
                                            <StoreIcon />
                                        </Avatar>
                                    </ListItemAvatar>
                                    <ListItemText
                                        primary="Single-line item"
                                        secondary={'Secondary text'}
                                    />
                                    <ListItemSecondaryAction>
                                        <IconButton aria-label="Add" onClick={this.handleAdd} style={{ display: 'block', margin: 'auto' }}>
                                            <PersonAddIcon />
                                        </IconButton>
                                    </ListItemSecondaryAction>
                                </ListItem>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>

                
            </div>

        )
    }
}
export default MarketRequest;
