import {RequestTool} from '../Tools/RequestTool';

const GetAll= () =>
    RequestTool.GetRequest("https://localhost:6001/api/Education/GetAll");

const Add= (_item) => RequestTool.PostRequest("https://localhost:6001/api/Education/Add",_item);

const Update=(_item) => RequestTool.PutRequest("https://localhost:6001/api/Education/Update",_item);

const AddUser=(_item) => RequestTool.PostRequest("https://localhost:6001/api/JoinEducation/Add",_item);

const GetAllUsers=(_courseId) => RequestTool.GetRequest("https://localhost:6001/api/JoinEducation/GetAll/"+_courseId);

export const EducationService= {GetAll,Add,Update,AddUser,GetAllUsers}
