        UserService.GetAllItems(_userId).
            then(_response =>{
                if(!_response.success){
                    _dispatch(Fail(_response.exception));
                    toast.error(_response.Exception);
                    return;
                }
                _dispatch(Success(_response.data));
                toast.success("Market listesi getirildi");
                
               
