import React, { Component } from 'react'
import { connect } from 'react-redux';
import { TextField, Grid, Button, Modal } from '@material-ui/core';
import MarketAction from '../Actions/MarketAction';


class AddMarketItemComponent extends Component {

    state = {
        Subject: '',
        Description: '',
        StartDate: '',
        FinishDate: '',
        UserId: null,
        Item: ''
    }

    componentDidMount(){
        this.props.GetMarketItemById(this.props.MarketId);
        const {MarketItem}= this.props;
        state={
            
            Subject:MarketItem.Data.subject,
            Description: MarketItem.Data.description,
            StartDate: '',
            FinishDate: '',
            UserId: null,
            Item: ''

        }


    }

    render() {

        const {MarketItem}= this.props;

        return (
            <div className="add-marketlist" style={{ padding: 70 }}>
                {this.props.MarketId}
                <Grid container spacing={5} alignItems="center">
                    <Grid item>
                        <TextField
                            fullWidth
                            disabled={true}
                            //label="Başlangıç Tarihiiiiii"
                            type="date"
                            value={this.state.subject}
                            name="StartDate"
                            onChange={this.HandleChange}

                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}
                            fullWidth
                            label="Bitiş Tarihi"
                            type="date"
                            defaultValue="2017-05-24"
                            name="FinishDate"
                            onChange={this.HandleChange}
                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}
                            onChange={this.HandleChange}
                            name='Subject'
                            autoFocus
                            margin="dense"
                            label="Market Adı"
                            fullWidth
                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}
                            onChange={this.HandleChange}
                            name='UserId'
                            autoFocus
                            margin="dense"
                            label="Talebi Açan kişi"
                            fullWidth
                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}
                            onChange={this.HandleChange}
                            name='Description'
                            autoFocus
                            margin="dense"
                            label="Açıklama"
                            fullWidth
                        />
                    </Grid>

                    <Grid item>
                        <TextField

                            onChange={this.HandleChange}
                            name='Item'
                            autoFocus
                            margin="dense"
                            label="İstekler"
                            fullWidth
                        />
                    </Grid>
                    <Grid item>
                        <Button style={{ marginTop: 20 }} onClick={this.Add} color="primary" variant="contained"> Ekle</Button>
                    </Grid>

                </Grid>



            </div>
        )
    }
}
const MapStateToProps =_state => {
    const { MarketItem } = _state.MarketReducer;
    return { MarketItem };
}

const MapDispatchToProps = (_dispatch,_ownProps) =>{
    return {
        GetMarketItemById: (_id) => _dispatch(MarketAction.GetMarketItemById(_id))
    }
}

export const AddMarketItem = connect(MapStateToProps,MapDispatchToProps)(AddMarketItemComponent);
