import React, { Component } from 'react'
import { connect } from 'react-redux';
import MarketAction from '../Actions/MarketAction';

class MarketItemUpdateComponent  extends Component {
    state = {
        request:"",
        count:""
    }
    Update=(_id)=>{
        let updateState={
            
        }
    }

    render() {
        const {MarketItem}=this.props;
        if(!MarketItem.Data){
            return;
        }

        return (
            <div className="update-marketlist" style={{ padding: 70 }}>
            <Grid container spacing={5} alignItems="center">
                <Grid item>
                    <TextField
                        value={MarketItem.Data.startDate.toString().slice(0, 10)}
                        fullWidth
                        disabled={true}
                        label="Başlangıç Tarihi"
                        type="date"
                        name="StartDate"

                    />
                </Grid>
                <Grid item>
                    <TextField
                        disabled={true}
                        fullWidth
                        label="Bitiş Tarihi"
                        type="date"
                        value={MarketItem.Data.finishDate.toString().slice(0, 10)}
                        name="FinishDate"

                    />
                </Grid>
                <Grid item>
                    <TextField 
                    value={MarketItem.Data.subject}
                        disabled={true}
                        name='Subject'
                        autoFocus
                        margin="dense"
                        label="Market Adı"
                        fullWidth
                    />
                </Grid>
                <Grid item>
                    <TextField
                        disabled={true}
                        value={MarketItem.Data.username}
                        name='Username'
                        autoFocus
                        margin="dense"
                        label="Talebi Açan kişi"
                        fullWidth
                    />
                </Grid>
                <Grid item>
                    <TextField
                        disabled={true}
                        value={MarketItem.Data.description}
                        name='Description'
                        autoFocus
                        margin="dense"
                        label="Açıklama"
                        fullWidth
                    />
                </Grid>

                <Grid item>
                    <TextField value={this.state.request}

                        onChange={(_e) => this.setState({ request: _e.target.value })}
                        name='Item'
                        autoFocus
                        margin="dense"
                        label="İstekler"
                        fullWidth
                    />
                </Grid>
                <Grid item>
                    <TextField value={this.state.count}

                        onChange={(_e) => this.setState({ count: _e.target.value })}
                        name='Count'
                        autoFocus
                        margin="dense"
                        label="Adet"
                        fullWidth
                    />
                </Grid>
                <Grid item>
                    <Button style={{ marginTop: 20 }} onClick={this.Update(MarketItem.id)} color="primary" variant="contained"> Ekle</Button>
                </Grid>

            </Grid>



        </div>
        )
    }
}

const MapStateToProps =_state =>{
    const {MarketItem} =_state.MarketReducer;

    return {MarketItem};
}

const MapDispatchToProps=(_dispatch,_ownProps) =>{
    return {
        GetMarketById:(_id)=> _dispatch(MarketAction.GetMarketById(_id)),
    }
}
export const MarketItemUpdate=connect(MapStateToProps,MapDispatchToProps)(MarketItemUpdateComponent);
