import Constant from '../Constants/UserConstant';

const initialState = {
    UserCourses:{
        Data:null,
        Error:null,
        Loading:false
    },
    UserMarketItems:{
        Data:null,
        Error:null,
        Loading:false
    }

}

export default function EducationReducer(state = initialState,_action) {
    switch (_action.type) {

    case Constant.UserCoursesSuccess:
        return { 
            ...state,
             Currentcourses:{
                 Data:_action.Payload,
                 Error:null,
                 Loading:true
             }
        };
    
    
        
        

    default:
        return state
    }
}
