import React, { Component } from 'react'
import { connect } from 'react-redux';
import { TextField, Grid, Button, Modal } from '@material-ui/core';
import MarketAction from '../Actions/MarketAction';


class AddMarketItemComponent extends Component {

    state = { 
        request: "",
        count:""
     }

    Add = () => {
        let addState={
            Item: this.state.request,
            Count:this.state.count,
            MarketId:this.props.MarketId,
            UserId:localStorage.getItem("user_")
        }

        this.props.AddMarketItem(addState);

        console.log({ Item: this.state.request, MarketId: this.props.MarketId });
    }

    componentDidMount() {
        this.props.GetMarketItemById(this.props.MarketId);
    }

    componentDidUpdate(prevProps) {

        if (prevProps.MarketId !== this.props.MarketId) {
            this.props.GetMarketItemById(this.props.MarketId);
            this.setState({ request: "" });
        }
    }

    render() {

        const { MarketItem, AddedMarketItem } = this.props;

        if (!MarketItem.Data) {
            console.log("Data yok");
            return "";
        }
        if ( AddedMarketItem.Data && this.state.show) {
            this.hideModal();
            AddedMarketItem.Data = null;
        }

        return (
            <div className="add-marketlist" style={{ padding: 70 }}>
                <Grid container spacing={5} alignItems="center">
                    <Grid item>
                        <TextField
                            value={MarketItem.Data.startDate.toString().slice(0, 10)}
                            fullWidth
                            disabled={true}
                            label="Başlangıç Tarihi"
                            type="date"
                            //value={}
                            name="StartDate"

                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}
                            fullWidth
                            label="Bitiş Tarihi"
                            type="date"
                            value={MarketItem.Data.finishDate.toString().slice(0, 10)}
                            name="FinishDate"

                        />
                    </Grid>
                    <Grid item>
                        <TextField value={MarketItem.Data.subject}
                            disabled={true}

                            name='Subject'
                            autoFocus
                            margin="dense"
                            label="Market Adı"
                            fullWidth
                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}

                            name='UserId'
                            autoFocus
                            margin="dense"
                            label="Talebi Açan kişi"
                            fullWidth
                        />
                    </Grid>
                    <Grid item>
                        <TextField
                            disabled={true}

                            name='Description'
                            autoFocus
                            margin="dense"
                            label="Açıklama"
                            fullWidth
                        />
                    </Grid>

                    <Grid item>
                        <TextField value={this.state.request}

                            onChange={(_e) => this.setState({ request: _e.target.value })}
                            name='Item'
                            autoFocus
                            margin="dense"
                            label="İstekler"
                            fullWidth
                        />
                    </Grid>
                   
                    <Grid item>
                        <Button style={{ marginTop: 20 }} onClick={this.Add} color="primary" variant="contained"> Ekle</Button>
                    </Grid>

                </Grid>



            </div>
        )
    }
}

const MapStateToProps = _state => {

    const { MarketItem, AddedMarketItem } = _state.MarketReducer;
    return { MarketItem, AddedMarketItem };
}

const MapDispatchToProps = (_dispatch, _ownProps) => {
    return {
        GetMarketItemById: (_id) => _dispatch(MarketAction.GetMarketItemById(_id)),
        AddMarketItem: (_data) => _dispatch(MarketAction.AddMarketItem(_data))
    }
}

export const AddMarketItem = connect(MapStateToProps, MapDispatchToProps)(AddMarketItemComponent);
