import React, { Component } from 'react'
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import AddIcon from '@material-ui/icons/Add';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import FolderIcon from '@material-ui/icons/Folder';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import { green } from '@material-ui/core/colors';
import { History } from '../Tools/History';
import { IconButton, TableRow, Container } from '@material-ui/core';
import { AddEducation } from '../Pages/AddEducation';
import EducationAction from '../Actions/EducationAction';
import { connect } from 'react-redux';
import Modal from '../Components/Modal';


class EducationComponent extends React.Component {

    constructor() {
        super();
        this.state = {
            show: false
        };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }
    componentDidMount = () => {
        this.props.GetAll();

    }


    showModal = () => {
        this.setState({ show: true })
    }

    hideModal = () => {
        this.setState({ show: false })
    }

  


    render() {

        const { Currentcourses } = this.props;
        if (!Currentcourses) {
            console.log("Data yok");
            return;
        }

        const element_courses = courses => {
            return (
                courses.map(course => {
                    return(
                        <TableRow>
                            <ListItem>
                                <ListItemAvatar>
                                    <Avatar>
                                        <FolderIcon />
                                    </Avatar>
                                </ListItemAvatar>
                                <ListItemText
                                    primary={course.subject}
                                    secondary={'Secondary text'}
                                />
                                <ListItemSecondaryAction>
                                    <IconButton aria-label="Add" onClick={this.handleAdd} style={{ display: 'block', margin: 'auto' }}>
                                        <PersonAddIcon />
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                        </TableRow>
                    );
                })


            );
        }


        return (
            <div>
                <h3>Eğitim</h3>
                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <Modal show={this.state.show} handleClose={this.hideModal}>
                                    <Container>
                                        <div><AddEducation /></div>
                                    </Container>
                                </Modal>
                                <IconButton className="float-right" edge="end" aria-label="Add" onClick={this.showModal} style={{ display: 'block', margin: 'auto' }}>
                                    <AddIcon style={{ color: green[400] }} />
                                </IconButton>

                            </TableRow>
                        </TableHead>
                        <TableBody>

                            {element_courses(Currentcourses.Data)}
                            <TableRow>
                                <ListItem >
                                    <ListItemAvatar>
                                        <Avatar>
                                            <FolderIcon />
                                        </Avatar>
                                    </ListItemAvatar>
                                    <ListItemText
                                        primary="Single-line item"
                                        secondary={'Secondary text'}
                                    />
                                    <ListItemSecondaryAction>
                                        <IconButton aria-label="Add" onClick={this.handleAdd} style={{ display: 'block', margin: 'auto' }}>
                                            <PersonAddIcon />
                                        </IconButton>
                                    </ListItemSecondaryAction>
                                </ListItem>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>


            </div>

        )
    }
}

const MapStateToProps = _state => {
    const { Currentcourses} = _state.EducationReducer;
    return {Currentcourses};

}



const MapDispatchToProps = (_dispatch, _ownProps) => {
    return {
        GetAll: () => _dispatch(EducationAction.GetCurrentCourse())
    }
}

export const EducationRequest = connect(MapStateToProps, MapDispatchToProps)(EducationComponent);
