import React, { Component } from 'react'

import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import AddIcon from '@material-ui/icons/Add';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import FolderIcon from '@material-ui/icons/Folder';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import { green } from '@material-ui/core/colors';

import { IconButton, TableRow, Container, Button } from '@material-ui/core';
import SchoolIcon from '@material-ui/icons/School';
import { AddEducation } from '../Pages/AddEducation';
import EducationAction from '../Actions/EducationAction';
import { connect } from 'react-redux';
import Modal from '../Components/Modal';



class EducationComponent extends Component {

    constructor() {
        super();
        this.state = {
            show: false,
            StartDate: '',
            FinishDate: '',
            Subject: '',
            Mentor: ''


        };
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }
    componentDidMount() {
        this.props.GetCurrentCourse();
    }



    showModal = () => {
        this.setState({ show: true })
    }

    hideModal = () => {
        this.setState({ show: false })
    }

    MentorAdd = () => {

    }

    render() {

        const { Currentcourses, AddCourse } = this.props;
        if (!Currentcourses.Data) {
            console.log("Data yok");
            return " ";

        }
        if (AddCourse.Data && this.state.show) {
            this.hideModal();
            AddCourse.Data = null;
        }

        const element_courses = courses => {
            return (

                courses.map(course => {
                    return (
                        <TableRow>
                            <ListItem>
                                <ListItemAvatar>
                                    <Avatar>
                                        <FolderIcon />
                                    </Avatar>
                                </ListItemAvatar>
                                <ListItemText
                                    primary={course.subject}
                                    secondary={course.startDate + "  " + course.finishDate}
                                />
                                <IconButton onClick={() => { alert(course.subject + ' mentor olmak istiyor musunuz ?') }}>
                                    <SchoolIcon></SchoolIcon>
                                </IconButton>

                                <IconButton edge="end" aria-label="Add" onClick={this.handleAdd} style={{ display: 'block', margin: 'auto' }}>
                                    <PersonAddIcon />
                                </IconButton>


                            </ListItem>
                        </TableRow>
                    );
                })





            );
        }


        return (
            <div>
                <h3>Eğitim</h3>
                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <Modal show={this.state.show} handleClose={this.hideModal}>
                                    <Container>
                                        <AddEducation />
                                    </Container>
                                </Modal>
                                <IconButton className="float-right" edge="end" aria-label="Add" onClick={this.showModal} style={{ display: 'block', margin: 'auto' }}>
                                    <AddIcon style={{ color: green[400] }} />
                                </IconButton>

                            </TableRow>
                        </TableHead>
                        <TableBody>

                            {element_courses(Currentcourses.Data)}

                        </TableBody>
                    </Table>
                </TableContainer>


            </div>

        )
    }
}

const MapStateToProps = _state => {
    const { Currentcourses, AddCourse } = _state.EducationReducer;
    return { Currentcourses, AddCourse };

}



const MapDispatchToProps = (_dispatch, _ownProps) => {
    return {
        GetCurrentCourse: () => _dispatch(EducationAction.GetCurrentCourse()),
        WillBeMentor: (_data) => _dispatch(EducationAction.WillBeMentor(_data))
    }
}

export const EducationRequest = connect(MapStateToProps, MapDispatchToProps)(EducationComponent);
