import {RequestTool} from '../Tools/RequestTool';

var _UserApiUrl="https://localhost:6001";

const Login = (_username ,_password) => RequestTool.PostRequest(_UserApiUrl + "/api/login/validate", //url kısmı
    {
        //data kısmı
        "username":_username,
        "password":_password
    });

    export const AuthenticationService= { Login };

