import React, { Component } from 'react'
import { connect } from 'react-redux'
import UserAction from '../Actions/UserAction';

class ListMyCoursesComponent extends Component {

    componentDidMount(){
        this.props.GetCourses(this.props.UserId);
    }


    render() {
        const { UserCourses } = this.props;
        if (UserCourses.Data == null) {
            return "";
        }
        const list_item = items => {

            return (

                items.map(item => {

                    let button;
                    if (item.userId === parseInt(localStorage.getItem('user_'))) {
                        button = <Button variant="contained" color="secondary" size="small" onClick={() => this.showEditModal(item.id)}>Düzenle</Button>;
                    }
                    else {
                        button = " ";
                    }
                    return (
                        <TableRow >
                            <TableCell component="th" scope="row">
                                {item.marketName}
                            </TableCell>
                            <TableCell align="right"> {item.item}</TableCell>
                            <TableCell align="right"> {item.count}</TableCell>
                            <TableCell align="right"> {item.requestUserName}</TableCell>


                            <TableCell align="right">{button}</TableCell>

                        </TableRow>

                    )
                })
            );
        }

        const _items = list_item(UserCourses.Data);
        return (
            <div>
                <div className="marketlist" style={{ padding: 25 }}>

                    {/* {this.state.ItemId != -1 && (
                        <Modal show={this.state.showEditModal} handleClose={this.hideEditModal}>
                            <Container>
                                <MarketItemUpdate ItemId={this.state.ItemId} />
                            </Container>
                        </Modal>)} */}

                    <TableContainer component={Paper}>

                        <Table size="small" aria-label="a dense table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Market</TableCell>
                                    <TableCell align="right">Alınacaklar</TableCell>
                                    <TableCell align="right">Adet</TableCell>
                                    <TableCell align="right">Talebi Açan kişi</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {_items}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </div>
            </div>
        )
    }
    
}
const MapStateToProps =_state =>{
    const {UserCourses}=_state.UserReducer;
    return {UserCourses};
}

const MapDispatchToProps=(_dispatch,_ownProps) =>{
    return {
        GetCourses :(_userid) => _dispatch(UserAction.GetCourses(_userid))
    }
}
export const ListMyCourses=connect(MapStateToProps,MapDispatchToProps)(ListMyCoursesComponent);
